import React from 'react';
import { VinylRecord } from '../data/productData';

interface ProductDisplayProps {
  vinylRecords: VinylRecord[];
}

const ProductDisplay: React.FC<ProductDisplayProps> = ({ vinylRecords }) => {
  return (
    <div className="product-display">
      <div className="turntable-section">
        <div className="turntable">
          <div className="tonearm"></div>
        </div>
        <div className="records-stack">
          {vinylRecords.map((record, index) => (
            <div key={index} className="record">
              <div className="record-label">
                {record.artist} - {record.album}
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="accessories-section">
        <div className="speaker">
          <div className="woofer"></div>
          <div className="tweeter"></div>
        </div>
        <div className="cable"></div>
        <div className="accessory-box">FLUANCE</div>
        <div className="accessory-box">FLUANCE</div>
        <div className="cleaning-brush"></div>
      </div>
    </div>
  );
};

export default ProductDisplay;
